import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.css";
import "./pages.css";
import axios from "axios";
import { setUsersession } from "../utility/Common";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [Error, setError] = useState("");
  const [Loading, setLoading] = useState(false);
  function UserEmail(e) {
    setEmail(e.target.value);
  }
  function UserPassword(e) {
    setPassword(e.target.value);
  }

  function HandSubmit(e) {
    e.preventDefault();
    alert(`login sucessfully`);
  }

  console.log(email);
  console.log(password);
  function login() {
    setError("null");
    setLoading(true);
    axios
      .post("http://restapi.adequateshop.com/api/authaccount/login", {
        Email: email.valueOf,
        Password: password.valueOf,
      })
      .then((response) => {
        setLoading(false);
        setUsersession(response.data.token, response.data.user);
        navigator("/login");
      });
  }

  return (
    <>
      <h4 className="text-center mt-4"> Welcome to login PageðŸ¥³</h4>

      <form className="align" style={{ width: "35%" }} onSubmit={HandSubmit}>
        <div className="form-group">
          <label for="exampleInputEmail1">Email address</label>
          <input
            type="email"
            className="form-control"
            id="InputEmail1"
            value={email}
            onChange={UserEmail}
            aria-describedby="emailHelp"
            placeholder="Enter email"
            required
          />
          <small id="emailHelp" className="form-text text-muted">
            {" "}
            Never share your email with anyone else.
          </small>
        </div>
        <div className="form-group">
          <label for="exampleInputPassword1">Password</label>
          <input
            type="password"
            className="form-control"
            id="exampleInputPassword1"
            value={password}
            required
            placeholder="Password"
            onChange={UserPassword}
          />
        </div>
        <div className="form-check">
          <input
            type="checkbox"
            className="form-check-input"
            required
            id="exampleCheck1"
          />
          <label className="form-check-label" for="exampleCheck1">
            Check me out
          </label>
        </div>
        <button type="submit" className="btn btn-primary">
          Submit
        </button>
      </form>
    </>
  );
}

export default Login;
