import React, { useState } from 'react';
import axios from 'axios';
import { setUserSession } from './Utils/Common';


function Login(props) {
  const [loading, setLoading] = useState(false);
  const email = useFormInput('');
  const password = useFormInput('');
  const [error, setError] = useState(null);


  // handle button click of login form
  const handleLogin = () => {
    setError(null);
    setLoading(true);
    axios.post('https://927e-103-141-51-42.in.ngrok.io/api/v1/login', { email: email.value, password: password.value }).then(response => {
      setLoading(false);
      setUserSession(response.data.token, response.data);
      props.history.push('/dashboard');
    }).catch(error => {
      setLoading(false);
      if (error)
      {
        console.log(error)
      } 

    });
  }

  return (
    <div className='container bg-dark text-white'> 
      Login<br /><br />
      <div>
        Username<br />
        <input type="text" {...email} autoComplete="new-password" />
      </div>
      <div style={{ marginTop: 10 }}>
        Password<br />
        <input type="password" {...password} autoComplete="new-password" />
      </div>
      {error && <><small style={{ color: 'red' }}>{error}</small><br /></>}<br />
      <input type="button" className='btn btn-primary' value={loading ? 'Loading...' : 'Login'} onClick={handleLogin} disabled={loading} /><br />
    </div>




  );
}
<form className="container card-4 light-grey text-blue margin">
<h2 className="w3-center">Contact Us</h2>

<div className="w3-row w3-section">
  <div className="w3-col" style="width:50px"><i className="w3-xxlarge fa fa-user"></i></div>
    <div className="w3-rest">
      <input class="w3-input w3-border" name="first" type="text" placeholder="First Name"/>
    </div>
</div>

<div className="row w3-section">
  <div className="col" style="width:50px"><i class="xxlarge fa fa-user"></i></div>
    <div className="w3-rest">
      <input clclassNameass="w3-input w3-border" name="last" type="text" placeholder="Last Name"/>
    </div>
</div>

<div class="w3-row w3-section">
  <div class="w3-col" style="width:50px"><i class="w3-xxlarge fa fa-envelope-o"></i></div>
    <div class="w3-rest">
      <input class="w3-input w3-border" name="email" type="text" placeholder="Email"/>
    </div>
</div>





<button class="w3-button w3-block w3-section w3-blue w3-ripple w3-padding">Send</button>

</form>
const useFormInput = initialValue => {
  const [value, setValue] = useState(initialValue);

  const handleChange = e => {
    setValue(e.target.value);
  }
  return {
    value,
    onChange: handleChange
  }
}

export default Login;
