import { BrowserRouter, Routes, Route } from "react-router-dom";
import Login from "./Login";
import PrivateRoute from "./Routes/Private";
import Dashboard from "./Dashboard";
import axios from "axios";
import {useState } from "react";
import { getToken as token } from "./Utils/Common";
function App() {

  const [Logged] = useState(token ? true : false);
  axios.interceptors.request.use(async (config) => {
    const expireAt = localStorage.getItem('expiresAt');
    let token = localStorage.getItem('authToken');
    if (dayjs(expireAt).diff(dayjs()) < 1) {
      const data = onGetForcedToken();
      token = typeof data === 'string' ? data : await data();
    }
    // setting updated token
    localStorage.setItem('authToken', token);
    return config;
  }, (err) => {
     console.log("error in getting ",err)
  });

  return (
    <div className="App">
      <BrowserRouter>
        <div>
          <div className="content">
            <Routes>
              <Route path="/" element={<Login />} />
              <Route element={<PrivateRoute isAuth={Logged} />}>
                <Route path="/dashboard" element={<Dashboard />} />
              </Route>
            </Routes>
          </div>
        </div>
      </BrowserRouter>
    </div>
  );
}

export default App;
